package operatormanish1;

import java.util.Scanner;

public class table {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		Scanner in=new Scanner(System.in);
		int m=in.nextInt();
		for(int i=1;i<=10;i++)
		{
			System.out.println(m+" x "+i+" = "+m*i); //print the table of number
		}

	}

}
